import React from "react";
import styled from "styled-components";
import rectangle from "./img/Rectangle."
const TextDiv = styled.div`
  position: absolute;
  left: 35px;
  top: 90px;
  white-space: nowrap;
  font-family: "Noto Sans";
  font-style: normal;
  font-weight: 600;
  font-size: 25px;
  line-height: 34px;
`;

const ScrollDiv = styled.div`
  margin-top: 160px;
  margin-left: 35px;
  overflow: scroll;

  /* 가로 스크롤 */
  overflow: auto;
  white-space: nowrap;

  ::-webkit-scrollbar {
    display: none;
  }
`;

const MenuDiv = styled.div`
  display: inline-block;
  width: 54px;
  height: 54px;
  background: #ffffff;
  border: 2px solid #4c88ff;
  border-radius: 50%;
  text-align: center;
  line-height: 54px;
  margin-right: 10px;

  font-family: Noto Sans;
  font-size: 15px;
  font-weight: 600;
`;

const DetailDiv = styled.div`
  width: 324px;
`;

const ImageDiv = styled.img`
  background-size: cover;
  width: 324px;
  height: 204px;

  margin-top: 100px;
  margin-left: 35px;
`;

const GameTitle = styled.div`
  position: absolute;
  left: 47px;
  margin-top: 2%;
  font-family: "Noto Sans";
  font-style: normal;
  font-weight: 600;
  font-size: 18px;
  line-height: 25px;
  /* identical to box height */

  color: #000000;
`;

const DetailText = styled.div`
  position: absolute;
  height: 20px;
  left: 47px;
  margin-top: 5%;
  font-family: "Noto Sans";
  font-style: normal;
  font-weight: 400;
  font-size: 15px;
  line-height: 20px;

  color: #000000;
`;

const Project = () => {
  const menus = [{ name: "" }];
  return (
    <div>
      <TextDiv>프로젝트</TextDiv>
      <ScrollDiv>
        <MenuDiv>전체</MenuDiv>
        <MenuDiv>6기</MenuDiv>
        <MenuDiv>5기</MenuDiv>
        <MenuDiv>4기</MenuDiv>
        <MenuDiv>3기</MenuDiv>
        <MenuDiv>2기</MenuDiv>
        <MenuDiv>1기</MenuDiv>
      </ScrollDiv>
      <DetailDiv>
        <ImageDiv src="/img/Rectangle.png" />
        <GameTitle>게임제목</GameTitle>
        <DetailText>설명</DetailText>
      </DetailDiv>
      <DetailDiv>
        <ImageDiv src="/img/Rectangle.png" />
        <GameTitle>게임제목</GameTitle>
        <DetailText>설명</DetailText>
      </DetailDiv>
      <DetailDiv>
        <ImageDiv src="/img/Rectangle.png" />
        <GameTitle>게임제목</GameTitle>
        <DetailText>설명</DetailText>
      </DetailDiv>
    </div>
  );
};

export default Project;
