import React from "react";
import styled from "styled-components";
import Pro1 from './Pro1.js';
import Pro2 from './Pro2';
import Pro3 from './Pro3';
import Pro4 from './Pro4';
import Pro5 from './Pro5';
import Pro6 from './Pro6';
import ProAll from './ProAll'
import {Routes, Route, Link} from 'react-router-dom'
const StyledLink = styled(Link)`
    text-decoration: none;
    color:black;
`;
const TextDiv = styled.div`
  position: absolute;
  left: 35px;
  top: 90px;
  white-space: nowrap;
  font-family: "Noto Sans";
  font-style: normal;
  font-weight: 600;
  font-size: 25px;
  line-height: 34px;
`;

const ScrollDiv = styled.div`
  margin-top: 160px;
  margin-left: 35px;
  overflow: scroll;

  /* 가로 스크롤 */
  overflow: auto;
  white-space: nowrap;

  ::-webkit-scrollbar {
    display: none;
  }
`;

const MenuDiv = styled.div`
  display: inline-block;
  width: 54px;
  height: 54px;
  background: #ffffff;
  border: 2px solid #4c88ff;
  border-radius: 50%;
  text-align: center;
  line-height: 54px;
  margin-right: 10px;

  font-family: Noto Sans;
  font-size: 15px;
  font-weight: 600;
`;

const DetailDiv = styled.div`
  width: 324px;
`;

const ImageDiv = styled.img`
  background-size: cover;
  width: 324px;
  height: 204px;

  margin-top: 100px;
  margin-left: 35px;
`;

const GameTitle = styled.div`
  position: absolute;
  left: 47px;
  margin-top: 2%;
  font-family: "Noto Sans";
  font-style: normal;
  font-weight: 600;
  font-size: 18px;
  line-height: 25px;
  /* identical to box height */

  color: #000000;
`;

const DetailText = styled.div`
  position: absolute;
  height: 20px;
  left: 47px;
  margin-top: 10%;
  font-family: "Noto Sans";
  font-style: normal;
  font-weight: 400;
  font-size: 15px;
  line-height: 20px;

  color: #000000;
`;

const Project = () => {
  const menus = [{ name: "" }];
  return (
    <div>

      <DetailDiv>
        <ImageDiv src="/img/올라프.jpg" style={{marginTop:'28px'}}/>
        <GameTitle>게임제목</GameTitle>
        <DetailText>설명</DetailText>
      </DetailDiv>
      <DetailDiv>
        <ImageDiv src="/img/올라프.jpg" />
        <GameTitle>게임제목</GameTitle>
        <DetailText>설명</DetailText>
      </DetailDiv>
      <DetailDiv>
        <ImageDiv src="/img/올라프.jpg" />
        <GameTitle>게임제목</GameTitle>
        <DetailText>설명</DetailText>
      </DetailDiv>
      
    </div>
  );
};

export default Project;
